-- DROP DATABASE IF EXISTS `sql11699000`;
-- CREATE DATABASE `sql11699000` CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
-- USE `sql11699000`;

DROP DATABASE IF EXISTS `balqan`;
CREATE DATABASE `balqan` CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE `balqan`;
CREATE TABLE `users`
(
    `user_id`      INT AUTO_INCREMENT PRIMARY KEY,
    `user_name`    VARCHAR(255),
    `user_surname` VARCHAR(255),
    `password`     VARCHAR(255),
    `email`        VARCHAR(255),
    `img`          VARCHAR(255),
    `DOF`          DATE,
    `phone`        VARCHAR(255),
    `country`      VARCHAR(255),
    `jobTitle`     VARCHAR(255),
    `YOE`          INT,
    `gender`       VARCHAR(15),
    `nationality`  VARCHAR(255),
    `skills`       TEXT,
    `ratingsStar`  TEXT,
    `activities`   TEXT
);
CREATE TABLE `items`
(
    `item_id`        INT AUTO_INCREMENT PRIMARY KEY,
    `name`           VARCHAR(255),
    `description`    TEXT,
    `price`          DECIMAL(10, 2),
    `stock_quantity` INT,
    `imgs_srcs`      TEXT,
    `min_days`       INT,
    `days`           INT,
    `max_days`       INT,
    `min_persons`    VARCHAR(255),
    `persons`        VARCHAR(255),
    `max_persons`    VARCHAR(255),
    `category`       VARCHAR(255),
    `title`          VARCHAR(255),
    `intro`          VARCHAR(255),
    `status`         VARCHAR(255),
    `added_time`     TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);
INSERT
INTO items(imgs_srcs,
           NAME,
           intro,
           title,
           description,
           price,
           stock_quantity,
           days,
           min_persons,
           max_persons,
           category,
           STATUS)
